import React, { useEffect } from "react";
import { Alert } from "react-native";
import styled from "styled-components/native";
import SocialLogin from "../components/SocialLogin";
import { useSelector, useDispatch } from "react-redux";
import { LoginAction } from "../actions/LoginAction";
import { readLoginInfo } from "../data/firebase";
import { ProgressAction } from "../actions/ProgressAction";

const Container = styled.SafeAreaView`
  flex: 1;
  align-items: center;
  background-color: ${({ theme }) => theme.background};
`;

const TestLoginContainer = styled.View`
  justify-content: center;
  align-items: center;
  position: absolute
  top: 14%;
  bottom: 70%
  right: 10%;
  left: 10%
`;

const TestLogin = styled.TouchableOpacity`
  background-color: ${({ theme }) => theme.button}
  justify-content: center;
  align-items: center;
  width: 150px;
  height: ${({ theme }) => theme.buttonHeight};
  border-radius: 15px;
`;

const TestLoginText = styled.Text`
  font-size: ${({ theme }) => theme.buttonTextSize};
  font-weight: bold;
  color: ${({ theme }) => theme.normalText};
`;

const TitleContainer = styled.View`
  position: absolute;
  left: 4%;
  right: 4%;
  top: 8%;
  bottom: 80%;
  justify-content: center;
  align-items: center;
`;

const Title = styled.Text`
  font-size: ${({ theme }) => theme.titleTextSize}
  color: ${({ theme }) => theme.normalText};
  font-weight: bold;
`;

const LogoContainer = styled.View`
  position: absolute;
  left: 4%;
  right: 4%;
  top: 30.99%;
  bottom: 49.89%;
  justify-content: center;
  align-items: center;
`;

const Logo = styled.View`
  height: 200px;
  width: 200px;
  border: solid 1px;
`;

const EmailLoginContainer = styled.View`
  position: absolute;
  left: 4%;
  right: 4%;
  top: 61.5%;
  bottom: 22.3%;
  justify-content: center;
  align-items: center;
`;

const SocialLoginContainer = styled.View`
  position: absolute;
  left: 4%;
  right: 4%;
  top: 84.68%;
  bottom: 6.03%;
  justify-content: center;
  align-items: center;
`;

const EmailLoginButton = styled.TouchableOpacity`
  width: 220px;
  height: 50px;
  align-items: center;
  justify-content: center
  background-color: ${({ theme }) => theme.button}
  border-radius: 20px
`;

const EmailLoginButtonText = styled.Text`
  font-size: ${({ theme }) => theme.buttonTextSize}
  color: ${({ theme }) => theme.normalText};
  font-weight: bold
`;

export default function Login({ navigation }) {
  const dispatch = useDispatch();

  async function _EmailLoginButton() {
    navigation.navigate("EmailLogin");
    console.log("Login page: move to email login page");
  }

  const email = "codepam2020@gmail.com";
  const password = "codepam2020";

  const progress = useSelector((state) => {
    return state.progress;
  });

  return (
    <Container>
      <TitleContainer>
        <Title>AND1</Title>
      </TitleContainer>
      <TestLoginContainer>
        <TestLogin
          onPress={async () => {
            dispatch(ProgressAction(true));
            const data = await readLoginInfo(email);
            dispatch(ProgressAction(false));
            dispatch(LoginAction(data.name, email, password));
            console.log("Login page: Login");
          }}
        >
          <TestLoginText>Test Login</TestLoginText>
        </TestLogin>
      </TestLoginContainer>
      <LogoContainer>
        <Logo />
      </LogoContainer>
      <EmailLoginContainer>
        <EmailLoginButton onPress={_EmailLoginButton}>
          <EmailLoginButtonText>이메일로 로그인 하기</EmailLoginButtonText>
        </EmailLoginButton>
      </EmailLoginContainer>
      <SocialLoginContainer>
        <SocialLogin />
      </SocialLoginContainer>
    </Container>
  );
}
